import './App.css';
import Entrada from './componentes/Entrada';
import Selector from './componentes/Selector';
import Boton from './componentes/Boton';

//No pude resolver el ponerle la accion a los botones, creo que tiene que ir el codigo acá

const tension = document.getElementsByClassName('tension');

function stylize(){

}

function App() {
  return (
    <div className="App">
      <div className='data-input-container'>
      <h1 className='tension-title'>Seleccionar tipo de electrificación</h1>

        <div className='tension-selector-container'>
          <Selector value={'tension'} id={'tension monofasico'} accion={stylize}>Monofásico</Selector>
          <Selector value={'tension'} id={'tension trifasico'}>Trifásico</Selector>
        </div>
        <Entrada>Potencia nominal</Entrada>
        <Entrada>Valor Fc</Entrada>
        <Entrada>Valor Fs</Entrada>
        <Entrada>Rendimiento</Entrada>
        <Entrada>Coseno de fí</Entrada>
        <h1 className='conduit-title'>Seleccionar tipo de Cañería</h1>
        <div className='conduit-selector-container'>
          <Selector value={'conduit'} id={'conduit enterrado'}>Enterrado</Selector>
          <Selector value={'conduit'} id={'conduit exterior'}>Exterior</Selector>
          <Selector value={'conduit'} id={'conduit aire'}>Aire</Selector>
        </div>
        <Entrada>Longitud del segmento en metros</Entrada>
        <Entrada>Máximo porcentaje de caída</Entrada>
        <Entrada>Valor Zt</Entrada>
        <Entrada>Valor K</Entrada>
        <div className='button-container'>
          <Boton identifier={'submit'}>Calcular</Boton>
          <Boton identifier={'clear'}>Limpiar</Boton>
        </div>
      </div>
    </div>
  );
}

export default App;


