import React from 'react';
import '../stylesheets/Selector.css';

/*Objetos*/
const d = document;
const selectBtn = d.getElementsByName('choiseBtn');
const btn = d.getElementsByClassName('btn');


/*Utilidad*/
selectBtn.addEventListener('click', (e) =>{
    e.target.classList.toggle('active');
});

function Selector(props){
    return(
        <div className='data-selector'>
            <input type='checkbox' value={props.value} id={props.id}></input>
            <label name='choiseBtn' className={'btn '+props.id} htmlFor={props.id}>{props.children}</label>
        </div>
    )
}





export default Selector;
/*Termina*/
