 let KV;

 //Acciones para entradas
 const applyValueRP = (e) => {
  RP = e.target.value;
 }

 const applyValueFC = (e) => {
  FC = e.target.value;
 }

 const applyValueFS = (e) => {
  FS = e.target.value;
 }

