  FiC = e.target.value;
 }

 const applyValueSL = (e) => {
  SL = e.target.value;
 }

 const applyValueMTF = (e) => {
  MTF = e.target.value;
 }

 const applyValueZtV = (e) => {
  ZtV = e.target.value;
 }

 const applyValueKV = (e) => {
  KV = e.target.value;
 }

 //Se crean referencias botones
 const btnCalc = useRef();

 //Acciones para botones
 const submit = (e) => {
  if(btnTension==='monophasic'){
    monophasicTension();
  }
  else if(btnTension==='triphasic'){
    triphasicTension();
  }
  else{
    error();
  }
 } 

