 let seccion6=`S=16 mm2, R=1.45 Ohm/km, X=0.0813 Ohm/km <br>`;
 let seccion7=`S=25 mm2, R=0.933 Ohm/km, X=0.078 Ohm/km <br>`;
 let seccion8=`S=35 mm2, R=0.663 Ohm/km, X=0.076 Ohm/km <br>`;
 let seccion9=`S=50 mm2, R=0.462 Ohm/km, X=0.0757 Ohm/km <br>`;
