 let XEC=[X1, X2, X3, X4, X5, X6, X7, X8, X9, X10, X11, X12]

 //Calculo para tensión trifásica
 const monophasicTension = () =>{
  console.log(btnTension);
  console.log(btnConduit);
  console.log(RP);
  console.log(FC);
  console.log(FS);
  console.log(Per);
  console.log(FiC);
  console.log(SL);
