 let XNO=`Demasiada corriente, no hay datos en tabla <br>`;
 let XEC=[X1, X2, X3, X4, X5, X6, X7, X8, X9, X10, X11, X12]

 //Calculo para tensión trifásica
 const monophasicTension = () =>{
  //Potencia demandada
  DemandedPowerOk=(RP*FC*FS);
  //Intensidad proyectada
  ProyectedIntensityOk=(DemandedPowerOk/(220*Per*FiC));

  //Se establecen los datos de Reactancia, Resistencia y Sección
  if (BtnConduit==='buried') {//Enterrado
    if (ProyectedIntensityOk<=MaxCurrentBuried[0]) {

        SEleg=0;
        REleg=0;
        XEleg=0;

    }else if (ProyectedIntensityOk<=MaxCurrentBuried[1]) {

        SEleg=1;
