    }else if (ProyectedIntensityOk<=MaxCurrentBuried[6]) {

        SEleg=6;
        REleg=6;
        XEleg=6;

    }else if (ProyectedIntensityOk<=MaxCurrentBuried[7]) {

        SEleg=7;
        REleg=7;
        XEleg=7;

    }else if (ProyectedIntensityOk<=MaxCurrentBuried[8]) {

        SEleg=8;
        REleg=8;
        XEleg=8;

    }else if (ProyectedIntensityOk<=MaxCurrentBuried[9]) {

        SEleg=9;
        REleg=9;
        XEleg=9;

    }else if (ProyectedIntensityOk<=MaxCurrentBuried[10]) {

        SEleg=10;
        REleg=10;
        XEleg=10;

    }else if (ProyectedIntensityOk<=MaxCurrentBuried[11]) {

        SEleg=11;
        REleg=11;
        XEleg=11;

    }else{

        SEleg=12;
        REleg=12;
        XEleg=12;

    }
} else if (BtnConduit==='exterior') {//cañeria externa
    if (ProyectedIntensityOk<=MaxCurrentExterior[0]) {

        SEleg=0;
        REleg=0;
        XEleg=0;

    }else if (ProyectedIntensityOk<=MaxCurrentExterior[1]) {

        SEleg=1;
