    }
}

//Porcentaje caída de tensión, Seno de Fí y longitud en Km
LengthKm=(SL/1000);
SenFi=(Math.cos(Math.asin(FiC)));
TensionFall=(LengthKm*ProyectedIntensityOk*((REC[REleg]*FiC)+(XEC[XEleg]*SenFi))*(100/220));

//Se determina la sección
if (TensionFall<=MTF) {
